"use strict";

Component({
  mixins: [],
  data: {
    $uiName: 'button'
  },
  props: {
    className: '',
    type: 'default',
    //primary|default|danger|primary-ghost|default-light
    disabled: false,
    size: 'default',
    //"large"|"default"|"small"
    loading: false,
    openType: '',
    formType: '',
    onDmTap: function onDmTap() {},
    onDmTapDisabled: function onDmTapDisabled() {},
    onDmCatchTap: null
  },
  didMount: function didMount() {},
  didUpdate: function didUpdate() {},
  didUnmount: function didUnmount() {},
  methods: {
    onDmTap: function onDmTap(e) {
      if (this.props.disabled) {
        this.props.onDmTapDisabled(e, {
          props: this.props,
          data: this.data
        });
        return false;
      }

      if (this.props.loading) {
        return false;
      }

      this.props.onDmTap(e, this);
    },
    onDmCatchTap: function onDmCatchTap(e) {
      if (this.props.onDmCatchTap) {
        if (this.props.disabled) {
          this.props.onDmTapDisabled(e, this);
          return false;
        }

        if (this.props.loading) {
          return false;
        }

        this.props.onDmCatchTap(e, this);
      }
    }
  }
});