"use strict";

Component({
  mixins: [],
  data: {
    $uiName: 'checkbox-item'
  },
  props: {
    className: '',
    arrowType: '',
    arrowSize: '',
    isChecked: false,
    // 是否选中状态，默认不选中
    itemName: '单行列表',
    disabled: false,
    hasArrowIcon: false,
    // 是否有右侧图标
    index: 0,
    data: null,
    onDmChange: function onDmChange() {},
    onDmExtraClick: function onDmExtraClick() {},
    onDmTap: function onDmTap() {}
  },
  didMount: function didMount() {},
  didUpdate: function didUpdate() {},
  didUnmount: function didUnmount() {},
  methods: {
    onDmChange: function onDmChange(res) {
      this.props.onDmChange(this.props.data, this.props.index, this, res.status);
    },
    onDmExtraClick: function onDmExtraClick(id, e, com) {
      this.props.onDmExtraClick(this.props.data, this.props.index, this, com.selected);
    }
  }
});