"use strict";

Component({
  mixins: [],
  data: {
    $uiName: 'crumbs'
  },
  props: {
    className: '',

    /** 面包屑导航数据集 */
    breadcrumbs: [],
    onDmCrumbsTap: function onDmCrumbsTap() {}
  },
  didMount: function didMount() {},
  didUpdate: function didUpdate() {},
  didUnmount: function didUnmount() {},
  methods: {
    onDmTap: function onDmTap(e) {
      var index = e.target.dataset.index; // 最后一项即当前页面，没有导航

      if (index !== this.props.breadcrumbs.length - 1) {
        this.props.onDmCrumbsTap(e, this, index);
      }
    }
  }
});