"use strict";

Component({
  mixins: [],
  data: {
    $uiName: 'notice-bar',
    showNotice: true
  },
  props: {
    className: '',
    type: 'info',
    // ['info', 'alert', 'warn']兼容mini-ddui
    value: '',
    iconType: '',
    isCloseable: false,
    onDmTap: function onDmTap() {},
    onDmClose: function onDmClose() {}
  },
  didMount: function didMount() {},
  didUpdate: function didUpdate() {},
  didUnmount: function didUnmount() {},
  methods: {
    onDmTap: function onDmTap(e) {
      this.props.onDmTap(e, this);
    },
    onDmClose: function onDmClose(e) {
      if (this.data.showNotice) {
        this.setData({
          showNotice: false
        });
      }

      this.props.onDmClose(e, this);
    }
  }
});