"use strict";

Component({
  mixins: [],
  data: {
    $uiName: 'radio-item'
  },
  props: {
    className: '',
    arrowType: 'details',
    arrowSize: 'medium',
    isChecked: false,
    // 是否选中状态，默认不选中
    itemName: '单行列表',
    disabled: false,
    hasArrowIcon: false,
    // 是否有右侧图标
    index: 0,
    data: null,
    onDmChange: function onDmChange() {},
    onDmExtraClick: function onDmExtraClick() {}
  },
  didMount: function didMount() {},
  didUpdate: function didUpdate() {},
  didUnmount: function didUnmount() {},
  methods: {
    onDmChange: function onDmChange(e) {
      this.props.onDmChange(this.props.data, this.props.index, this, this.props.isChecked);
    },
    onDmExtraClick: function onDmExtraClick(e) {
      this.props.onDmExtraClick(this.props.data, this.props.index, this, this.props.isChecked);
    }
  }
});